import { Component, OnInit } from '@angular/core';
import { CommentService } from './comments.service';
import { Comments } from './comments';

@Component({
  selector: 'app-comments',
  templateUrl: './comments.component.html',
  styleUrls: ['./comments.component.css']
})

export class CommentComponent  implements OnInit{
  comentarios: Comments[] = [];

  constructor(private commentsservice: CommentService){}

  ngOnInit(): void {
      this.CargarComentarios();
  }

  CargarComentarios(): void {
    this.commentsservice.getComments().subscribe((res: any) => { 

      // Recorrer el array usando forEach
      this.comentarios.forEach((comentario: Comments) =>{
      // Hacer algo con cada tarea, por ejemplo, mostrar su nombre
      console.log(comentario.id);
      })
    })
  }
}


<div class="tab-pane fade" id="comentarios">
    <div class="container mt-5">
        <h3>Comentarios</h3>
        <div class="row mt-3">
            <!-- Columna de 4 espacios -->
            <div class="col-md-4" *ngFor="let comentario of comentarios">
                <div class="card">
                    <div class="card-body">
                        <h5 class="comentario">{{ comentario.text0 }}</h5>
                        <h6>{{ comentario.email }}</h6>
                        <div class="col-md-10">
                            <p class="card-text">{{ comentario.body }}</p>
                        </div>
                        <ul class="list-group">
                            <li class="list-group-item"><strong>User:</strong> {{ comentario.id }}</li>
                            <li class="list-group-item"><strong>ID:</strong> {{ comentario.postId }}</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
