import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CommentService {
  private apiUrl = 'https://jsonplaceholder.typicode.com commen';

  constructor(private http: HttpClient) { }

  // Obtener todos los comentarios
  getComments(): Observable<Comment[]> {
    const url = `${this.apiUrl}/comments`;
    return this.http.get<Comment[]>(url);
  }

  // Obtener un comentario por su ID
  getCommentById(id: number): Observable<Comment> {
    const url = `${this.apiUrl}/comments/${id}`;
    return this.http.get<Comment>(url);
  }

  // Crear un nuevo comentario
  createComment(comment: Comment): Observable<Comment> {
    const url = `${this.apiUrl}/comments`;
    return this.http.post<Comment>(url, comment);
  }

  // Actualizar un comentario existente
  updateComment(comment: Comment): Observable<Comment> {
    const url = `${this.apiUrl}/comments/${comment.id}`;
    return this.http.put<Comment>(url, comment);
  }

  // Eliminar un comentario por su ID
  deleteComment(id: number): Observable<any> {
    const url = `${this.apiUrl}/comments/${id}`;
    return this.http.delete(url);
  }
}
