import { Component, OnInit } from '@angular/core';
import { PostService } from './post.service';
import { Post } from './post';

@Component({
  selector: 'app-users',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class postComponent  implements OnInit{
  usuarios: Post[] = [];

  constructor(private serviciousuarios: postService){}

  ngOnInit(): void {
      this.CargarUsuarios();
  }

  CargarUsuarios(): void {
    this.serviciodeusuarios.traerUsuarios().subscribe((res: any) =>{
      this.usuarios= res;

      // Recorrer el array usando forEach
      this.usuarios.forEach((usuario: post) =>{
      // Hacer algo con cada tarea, por ejemplo, mostrar su nombre
      console.log(usuario.address.city);
      })
    })
  }
}
