import { CommentService } from './comments.service';
import { Comments } from './comments';

@Component({
  selector: 'app-comments',
  templateUrl: './comments.component.html',
  styleUrls: ['./comments.component.css']
})

export class CommentsComponent  implements OnInit{
  comentarios: Comments[] = [];

  constructor(private commentsservice: CommentService){}

  ngOnInit(): void {
      this.CargarComentarios();
  }

  CargarComentarios(): void {
    this.commentsservice.getComments().subscribe((res: any) => { 

      // Recorrer el array usando forEach
      this.comentarios.forEach((comentario: Comments) =>{
      // Hacer algo con cada tarea, por ejemplo, mostrar su nombre
      console.log(comentario.id);
      })
    })
  }
}
